sys_web_service.script:
(function scriptedWebServiceOperation(request, response) {

    // Add your code here
    var payload = new XMLDocument2();
    payload.createElement("deviceMasterData");
    _addParameter(payload, 'deviceType', '1', 'HP COLOR LASERJET 3550');
    _addParameter(payload, 'manufacturer', '1', 'Hewlett Packard');
    _addParameter(payload, 'collectProtocol', '1', 'Snmp');
    var result = payload.getDocumentElement();
	return result;

})(request, response);

function _addParameter(payload, elemnetName, id, name) {
	if(elemnetName)
		var el = payload.createElement(elemnetName);
	if(id)
		el.setAttribute("id", id);
	if (name)
		el.setAttribute("name", name);
	return el;
}
